//----------------------------------------------------------------------
// Button layout
//----------------------------------------------------------------------
$btn-height-small : rem(44);
$btn-height-large : rem(56);

@mixin f-button-layout($btn-size: medium, $icon-size: default) {
    @include transition((background-color, border-color, color, box-shadow));

    border-radius: $border-radius-small;
    font-size: rem(16);
    font-weight: $font-main-bold;
    min-width: rem(152);
    padding: 0 rem(10);
    @if ($btn-size == medium) {
        $icon-gap: rem(8) !global;
        $icon-font-size: rem(16) !global;

        height: $btn-height-small;
        line-height: $btn-height-small;
    } @else if $btn-size == large {
        $icon-gap: rem(12) !global;
        $icon-font-size: rem(32) !global;

        height: $btn-height-large;
        line-height: $btn-height-large;
    }
    @if $icon-size == large {
        $icon-font-size: rem(32) !global;
    }

    &::before {
        margin-right: $icon-gap;
    }

    &::after {
        margin-left: $icon-gap;
    }

    &::before,
    &::after {
        line-height: inherit;
    }

    &[class*="i-svg-"],
    &[class*="i-social-"],
    &[class*="i-font-"] {
        text-align: initial;
    }

    &[class*="i-svg-"] {
        &::before,
        &::after {
            font-size: rem(43);
        }
    }

    &[class*="i-social-"] {
        &::before,
        &::after {
            font-size: rem(32);
        }
    }

    &:hover {
        text-decoration: none;
    }
}

@mixin f-state-disabled-style($bg-color : color(grey, 3), $color :color(grey)) {
    @include f-state-disabled {
        background-color: $bg-color;
        border-color: $bg-color;
        box-shadow: none;
        color: $color;
    }
}

@mixin f-button-shadow-style(
    $shadow-size-default: 0,
    $shadow-size-hover: -2px,
    $shadow-size-active: 1px
) {
    @include f-state-hover {
        box-shadow: inset 0 #{$shadow-size-hover} 0 color(black, $opacity: shadow);
    }
    @include f-state-active {
        box-shadow: inset 0 #{$shadow-size-active} 0 color(black, $opacity: shadow);
    }

    box-shadow: inset 0 #{$shadow-size-default} 0 color(black, $opacity: shadow);
}

.f-button,
%f-button {
    @include f-button-layout();
    @include f-button {
        background-color: color(grey, 4);
        border-color: color(grey, 2);
        color: color(black);
    }
    @include f-state-hover {
        background-color: color(blue, light);
        border-color: color(blue, light);
        color: color(white);
        text-decoration: none;
    }
    @include f-state-active {
        background-color: color(blue, light);
        border-color: color(blue, light);
        box-shadow: inset 0 2px 0 color(black, $opacity: shadow);
        color: color(white);
    }
    @include f-state-disabled-style();
}

.f-button-link,
%f-button-link {
    @include f-button-layout();
    @include f-button {
        background-color: color(white);
        border-color: transparent;
        color: color(blue);
    }
    @include f-state-hover {
        background-color: color(grey, 4);
        border-color: color(grey, 4);
    }
    @include f-state-active {
        background: color(blue, light);
        border-color: color(blue, light);
        box-shadow: inset 0 2px 0 color(black, $opacity: shadow);
    }
    @include f-state-disabled-style();
}

@mixin cta-primary-style() {
    @include f-button {
        background-color: color(green);
        border: 0;
        color: color(white);
        font-weight: $font-main-bold;
    }
    @include f-button-shadow-style(
        $shadow-size-default: -2px,
        $shadow-size-hover: -4px,
        $shadow-size-active: 2px
    );
    @include f-state-disabled-style();
}

.f-cta-primary,
%f-cta-primary {
    @include f-button-layout(large);
    @include cta-primary-style;
}

.f-cta-primary--small,
%f-cta-primary--small {
    @include f-button-layout(medium, large);
    @include cta-primary-style;
}

@mixin cta-secondary-style() {
    @include f-button() {
        background-color: color(grey, 4);
        border-color: color(black, $opacity: shadow);
        color: color(black);
    }
    @include f-button-shadow-style();
    @include f-state-disabled-style();
}

.f-cta-secondary,
%f-cta-secondary {
    @include f-button-layout(large);
    @include cta-secondary-style();
}

.f-cta-secondary--small,
%f-cta-secondary--small {
    @include f-button-layout(medium, large);
    @include cta-secondary-style();
}

@mixin cta-icon-layout() {
    min-width: rem(64);
    font-size: rem(22);

    &[class*="i-"] {
        text-align: center;
    }

    .f-button__text,
    .f-cta__text {
        @include visually-hidden;
    }

    &::before,
    &::after {
        margin-right: 0;
    }
}

.f-cta-icon-primary,
%f-cta-icon-primary {
    @include f-button-layout(large);
    @include cta-icon-layout();
    @include f-button-shadow-style();
    @include f-state-disabled-style($color: color(white));
    @include f-button() {
        background: color(blue, light);
        border-color: color(blue, light);
        color: color(white);
    }
}

.f-cta-icon-secondary,
%f-cta-icon-secondary {
    @include f-button-layout(large);
    @include cta-icon-layout();
    @include f-button-shadow-style();
    @include f-state-disabled-style($color: color(white));
    @include f-button() {
        background-color: color(grey, 4);
        border-color: color(grey, 2);
        color: color(grey, dim);
    }
    @include f-state-active {
        background-color: color(red);
        color: color(white);
    }
}

@mixin cta-icon-squircle-layout($btn-type: large, $btn-size: rem(48), $icon-size: rem(21)) {
    @if $btn-type == small {
        $btn-size : baseline(5);
        $icon-size: rem(18);
    }

    border-radius: 45%;
    font-size: 0;
    height: $btn-size;
    text-align: center;
    width: $btn-size;

    &::before,
    &::after {
        font-size: $icon-size;
        line-height: $btn-size / $icon-size;
    }
}

@mixin cta-icon-squircle-style() {
    @include transition((background-color, color));
    @include f-state-hover {
        background-color: color(green, light);
    }
    @include f-state-active {
        background-color: color(green, light);
    }

    background-color: color(blue, light);
    color: color(white);
}

.f-cta-icon-squircle,
%f-cta-icon-squircle {
    @include cta-icon-squircle-layout();
    @include f-state-disabled-style();
    @include cta-icon-squircle-style();
}

.f-cta-icon-squircle--small,
%f-cta-icon-squircle--small {
    @include cta-icon-squircle-layout(small);
    @include f-state-disabled-style();
    @include cta-icon-squircle-style();
}

.f-button-clear {
    @include f-button-clear;
}

.b-link,
%b-link {
    font-size: $font-size-regular;
    font-weight: $font-main-bold;
    line-height: $line-height-medium;
    color: $color-link;

    &:hover {
        text-decoration: underline;
    }
}

.b-link-text {
    @include transition;

    align-items: center;
    border: 1px solid color(grey, 2);
    border-radius: $border-radius-small;
    color: color(black);
    display: flex;
    font-size: $font-size-regular;
    font-weight: $font-main-bold;
    justify-content: center;
    line-height: 1;
    min-height: baseline(8);
    padding: baseline(2) baseline(3);

    @media (--extra-small) {
        padding: baseline(1.5);
    }

    &:hover {
        box-shadow: $box-shadow-regular;
        border-color: color(white);
        text-decoration: none;
    }

    &[class*=i-] {
        justify-content: space-between;

        &::before,
        &::after {
            font-size: baseline(6);
        }

        &::before {
            margin: 0 baseline(2) 0 baseline(-1);
        }

        &::after {
            margin: 0 baseline(-1) 0 baseline(2);
        }

        @media (--extra-small) {
            justify-content: center;

            &::after,
            &::before {
                display: none;
            }
        }
    }
}

.b-link-group {
    margin: baseline(3) 0;

    &__list {
        display: flex;

        &:first-child {
            .b-link-group__item {
                &:first-child {
                    border-radius: $border-radius-small 0 0 0;
                }

                &:last-child {
                    border-radius: 0 $border-radius-small 0 0;
                }
            }
        }

        &:last-child {
            .b-link-group__item {
                &:first-child {
                    border-radius: 0 0 0 $border-radius-small;
                }

                &:last-child {
                    border-radius: 0 0 $border-radius-small 0;
                }
            }
        }
    }

    &__item {
        @include transition;

        background-color: color(white);
        box-shadow: inset 0 0 0 1px color(grey, 2);
        flex: 1;
        margin: 0 -1px -1px 0;
        position: relative;

        &:hover {
            box-shadow: inset 0 0 0 1px color(blue, light);
            background-color: color(blue, tint);
            z-index: $z-min;
        }
    }

    &__link {
        @include transition(color);

        color: color(black);
        display: block;
        font-size: $font-size-medium;
        font-weight: $font-main-bold;
        line-height: $line-height-medium;
        padding: baseline(2.5);
        text-align: center;

        &:hover {
            color: color(blue);
            text-decoration: none;
        }
    }
}

