$color-palette: (
    default       : (base: #000),
    black         : (base: #000),
    blue          : (base: #1450a0, light: #2aace2, bright: #66d1ff, tint: #d7f1fd),
    green         : (base: #6da10c, light: #bed731, tint: #f0f7cf),
    grey          : (base: #666666, 2: #dbdbdb, 3: #ebebeb, 4: #f7f7f7, 5: #999999),
    orange        : (base: #e67700, deep: #e65d00),
    pink          : (base: #f599b8, tint: #ffdfea),
    purple        : (base: #b986ee, light: #dab8ff, tint: #f2e6ff, deep: #9554e5),
    red           : (base: #de004d, light: #ff3a33),
    teal          : (base: #45cab9, light: #4ddbc8, tint: #dcfffa),
    yellow        : (base: #fed204, tint: #fffddc),
    white         : (base: #fff, cream: #f6ffc9),
    none          : (base: transparent)
);

$color-opacity: (
    original: 1,
    light: 0.8,
    overlay: 0.7,
    dark: 0.4,
    shadow: 0.15
);

// Global colors
$color-text              : color(black);
$color-link              : color(blue);
$color-error             : color(red);
$color-success           : color(green);
$color-input-border      : color(grey, 2);

//----------------------------------------------------------------------
// Fonts
//----------------------------------------------------------------------

// Base font size
$baseline              : get-baseline(8px);

// Font families
$font-family-main      : "Nunito Sans", "El Messiri", sans-serif;
$font-family-akcent    : "Shag Expert", "Baloo Bhaijaan", sans-serif;

// Letter spacing
$letter-spacing-main   : rem(0.25);

// Font weight
$font-main-regular     : 300;
$font-main-medium      : 600;
$font-main-bold        : 700;
$font-akcent-regular   : 400;

// Font style
$font-main-oblique     : oblique;

//----------------------------------------------------------------------
// Height
//----------------------------------------------------------------------

$height-form-field: baseline(6);

//----------------------------------------------------------------------
// Simple Search Animation
//----------------------------------------------------------------------

$header-icon-size: baseline(3);
$header-small-icon-gutter: baseline(2);

$header-search-height: rem(44);
$header-search-gutter: rem(10);
$header-search-animation-duration: $transition-duration-regular;
$header-search-animation-delay: $transition-duration-short;

$header-search-suggestions-bg: color(grey, 4);

//----------------------------------------------------------------------
// Block Corner Radius
//----------------------------------------------------------------------

$border-radius-regular: rem(8);
$border-radius-small: rem(4);

//----------------------------------------------------------------------
// Block Shadow
//----------------------------------------------------------------------

$box-shadow-small: 0 rem(-2) rem(11) 0 color(black, $opacity: shadow);
$box-shadow-regular: 0 rem(6) rem(30) rem(-8) color(black, $opacity: 0.6);
$box-shadow-big: 0 rem(8) rem(40) rem(-6) color(black, $opacity: 0.6);

//----------------------------------------------------------------------
// Zoom
//----------------------------------------------------------------------

$zoom-nav-bg: color(grey, 4);

//----------------------------------------------------------------------
// Forms
//----------------------------------------------------------------------

$width-form-regular: rem(300);

@mixin icon-styles($size: 1em) {
    display: inline-block;
    font-family: "icon";
    font-size: $size;
    -webkit-font-smoothing: antialiased;
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    line-height: 1;
    -moz-osx-font-smoothing: grayscale;
    text-decoration: none;
    text-transform: none;
    vertical-align: top;
}

%icon {
    @include icon-styles;
}

@function icon-char($filename) {
    $char: "";
    @if $filename == add {
        $char: "";
    }
    @if $filename == basket-filled {
        $char: "";
    }
    @if $filename == basket {
        $char: "";
    }
    @if $filename == calendar {
        $char: "";
    }
    @if $filename == chevron-down {
        $char: "";
    }
    @if $filename == chevron-left {
        $char: "";
    }
    @if $filename == chevron-right {
        $char: "";
    }
    @if $filename == chevron-up {
        $char: "";
    }
    @if $filename == close {
        $char: "";
    }
    @if $filename == email {
        $char: "";
    }
    @if $filename == exclamation {
        $char: "";
    }
    @if $filename == filter {
        $char: "";
    }
    @if $filename == handle {
        $char: "";
    }
    @if $filename == heart-filled {
        $char: "";
    }
    @if $filename == heart {
        $char: "";
    }
    @if $filename == info {
        $char: "";
    }
    @if $filename == location-filled {
        $char: "";
    }
    @if $filename == location {
        $char: "";
    }
    @if $filename == lock {
        $char: "";
    }
    @if $filename == menu {
        $char: "";
    }
    @if $filename == minus {
        $char: "";
    }
    @if $filename == phone {
        $char: "";
    }
    @if $filename == search {
        $char: "";
    }
    @if $filename == show {
        $char: "";
    }
    @if $filename == sort {
        $char: "";
    }
    @if $filename == star {
        $char: "";
    }
    @if $filename == tick {
        $char: "";
    }
    @if $filename == user-filled {
        $char: "";
    }
    @if $filename == user {
        $char: "";
    }
    @if $filename == zoom {
        $char: "";
    }
    @if $filename == select-arrows {
        $char: "";
    }
    @if $filename == star-rotated {
        $char: "";
    }
    @if $filename == play-arrow {
        $char: "";
    }
    @if $filename == warning {
        $char: "";
    }
    @if $filename == list {
        $char: "";
    }
    @if $filename == location-marker {
        $char: "";
    }
    @if $filename == location-marker-star {
        $char: "";
    }
    @if $filename == direction {
        $char: "";
    }
    @if $filename == question {
        $char: "";
    }
    @if $filename == add-circle {
        $char: "";
    }
    @if $filename == share {
        $char: "";
    }
    @return $char;
}

@mixin icon($filename, $insert: before, $extend: true, $size: 16px) {
    &:#{$insert} {
        content: icon-char($filename);
        @if $extend {
            @extend %icon;
        } @else {
            @include icon-styles($size);
        }
        @content;
    }
}

@mixin i-svg() {
    display: inline-block;
    vertical-align: middle;
    font-size: 1em;
    line-height: 1;
    background-image: url("../images/sprite.svg");
    background-repeat: no-repeat;
    background-size: 39.671875em 41.359375em;
}
[class*="i-svg--"]::before,
[class*="i-svg-after--"]::after {
    @include i-svg;
}

@mixin i-svg--assembly() {
    content: "";
    background-position: 0em 0em/*rtl:ignore*/;
    width: 1em;
    height: 1em;
}
.i-svg--assembly::before,
.i-svg-after--assembly::after {
    @include i-svg--assembly;
}

