.f-select,
.f-textarea,
.f-textinput,
%f-select,
%f-textarea,
%f-textinput {
    @include f-textinput {
        border: 1px solid $color-input-border;
        border-radius: rem(4);
        color: color(black);
        font-family: $font-family-main;
        font-size: $font-size-small;
        font-weight: $font-main-regular;
        min-height: $height-form-field;
        padding: rem(10) rem(40) rem(10) rem(12);
        transition: border-color 0.1s linear;
    }
}

.f-textarea,
.f-textinput,
%f-textarea,
%f-textinput {
    background: color(white);

    &::placeholder {
        @include transition(color);
    }

    @include f-state-error {
        border-color: $color-error;
    }

    @include f-state-success {
        border-color: color(green, light);
    }

    @include f-state-focus {
        border-color: color(blue, light);

        &::placeholder {
            color: color(none);
        }
    }

    @include f-state-validated {
        background-image: url("../images/single-icons/form-input-states.svg");
        background-repeat: no-repeat;
        background-color: color(white);
        background-position: right -#{rem(596) - $height-form-field + rem(1)}; // rem(1) - border compensation
        background-size: rem(44) rem(596);

        .f-field.ltr &,
        &.ltr {
            background-position-x: right rtl-ignore();
            padding-right: rem(40) rtl-ignore();
        }
    }

    @include f-state-success {
        background-position-y: - rem(1); // rem(1) - border compensation
    }
}

.f-textarea,
%f-textarea {
    min-height: rem(100);
}

$checkbox-size: baseline(3);
$checkbox-glyph-size: baseline(1.5);
$checkbox-label-padding: baseline(1);

.f-checkbox,
.f-radio {
    @include f-checkbox {
        background-color: color(white);
        border: 1px solid color(grey, 3);
        border-radius: $border-radius-small;
        height: $checkbox-size;
        margin-right: rem(10);
        width: $checkbox-size;
    }

    ~ .f-label {
        @include f-checkbox-label;

        display: inline-flex;
        line-height: 1.3;
        vertical-align: top;

        &::before {
            flex-shrink: 0;
        }
    }

    &:checked ~ .f-label {
        font-weight: $font-main-medium;

        .non-touch &:hover {
            background-color: color(green, tint);
        }
    }
}

.f-checkbox {
    ~ .f-label {
        position: relative;

        &::before {
            @include transition((background-color, border-color));
        }

        &::after {
            @include icon-styles($checkbox-glyph-size);
            @include transition((max-width, visibility));

            color: color(white);
            content: icon-char(tick);
            height: $checkbox-size;
            left: $checkbox-label-padding + ($checkbox-size - $checkbox-glyph-size) / 2;
            line-height: $checkbox-size / $checkbox-glyph-size;
            max-width: 0;
            overflow: hidden;
            position: absolute;
            top: $checkbox-label-padding;
            visibility: hidden;
        }
    }

    &:checked ~ .f-label {
        &::before {
            background-color: color(green, light);
            border-color: color(green, light);
        }

        &::after {
            max-width: $checkbox-glyph-size;
            visibility: visible;
        }
    }
}

.f-select,
%f-select {
    background-color: color(grey, 4);
    height: $height-form-field;
    line-height: $height-form-field;
    padding: 0 rem(22) 0 rem(12);
    white-space: nowrap;

    @include f-state-error {
        &,
        ~ .select2-container .select2-selection {
            border-color: $color-error;
        }
    }

    @include f-state-focus {
        &,
        ~ .select2-container .select2-selection {
            border-color: color(blue, light);
        }
    }
}

.f-field--select .f-field__inner,
.select2-selection__arrow {
    @include icon(select-arrows, before) {
        color: color(grey);
        font-size: rem(18);
        pointer-events: none;
        position: absolute;
        right: rem(10);
        top: baseline(2);
    }
}

.f-field--select .f-field__inner-image,
.select2-results__option-image {
    border-radius: 50%;
    box-shadow: 0 0 1px 0 color(grey, 2) inset;
    flex-shrink: 0;
    height: baseline(2);
    margin-right: baseline(1);
    overflow: hidden;
    width: baseline(2);
}

.f-field--select .f-field__inner-image {
    margin-left: baseline(1.5);

    + .f-select {
        padding-left: baseline(4.5);
    }
}

.select2-selection {
    @include transition((background-color, border-color, color));
    @include f-state-focus {
        border-color: color(grey, 2);
    }

    &__arrow::before {
        @include transition(color);
    }

    .select2-container--open.select2-container--open & { // double class to raise priority of styles for open state
        background-color: color(blue, light);
        border-color: color(blue, light);

        &,
        &__arrow::before {
            color: color(white);
        }
    }
}

.select2-dropdown {
    background-color: color(white);
    border-radius: $border-radius-small;
    box-shadow: $box-shadow-regular;
    transform: translateY(baseline(1));
    overflow: hidden;
    padding: baseline(1);

    &--above {
        transform: translateY(baseline(-1));
    }
}

.select2-search {
    margin-bottom: baseline(1);

    &__field {
        @extend %f-textinput;
    }
}

.select2-results {
    &__options {
        @include brand-scrollbar();

        padding-right: rem(1);
    }

    &__option {
        border-radius: $border-radius-small;
        font-size: $font-size-small;
        line-height: 1;
        padding: baseline(1);

        &--highlighted,
        .non-touch &:hover {
            background-color: color(grey, 4);
        }
    }

    &__option + &__option {
        margin-top: baseline(1);
    }

    &__message {
        background-color: color(pink, tint);
        font-weight: $font-main-medium;
        pointer-events: none;
    }
}

.b-accordion {
    &__header {
        background-color: color(grey, 4);
        border-radius: $border-radius-small;
        font-weight: $font-main-bold;
        margin-bottom: rem(1);
        padding: baseline(2);

        @media (--extra-small) {
            border-radius: 0;
        }

        &::after {
            font-size: rem(16);
            margin-right: baseline(2);
        }
    }

    &__header-text {
        @include b-toggle-button(
            $active-state-modifier: ".b-accordion__header.ui-accordion-header-active, .b-accordion__header" + $const-expanded,
            $parent-selector: true
        );

        line-height: baseline(2);
        padding-right: 2rem;
    }

    &__values {
        @include transition(max-height);

        color: color(grey);
        font-size: $font-size-small;
        line-height: baseline(2);

        &::before {
            content: "";
            display: block;
            height: baseline(0.5);
        }
    }

    &__content {
        padding: baseline(2);
    }

    &--as-tab {
        @media (--from-small) {
            .b-accordion__header {
                background-color: transparent;
                border-radius: 0;
                color: color(grey);
                font-weight: $font-main-regular;

                &:hover::after {
                    background-color: color(grey, 2);
                }

                &::after {
                    border-radius: rem(4) rem(4) 0 0;
                    height: rem(4);
                    margin: 0;
                    position: absolute;
                }

                &.ui-accordion-header-active {
                    color: color(black);
                    font-weight: $font-main-bold;

                    &::after {
                        background-color: color(grey, 2);
                    }
                }
            }

            .b-accordion__header-text {
                padding-right: 0;

                &::before,
                &::after {
                    display: none;
                }
            }

            .b-accordion__content {
                border-top: 1px solid color(grey, 2);
                padding: baseline(3) 0 0;

                ~ .b-accordion__content {
                    display: none;
                }
            }
        }

        @media (--small) {
            .b-accordion__header {
                padding: baseline(2.5) rem(24);
            }
        }

        @media (--from-medium) {
            .b-accordion__header {
                padding: baseline(2.5) rem(38);
            }
        }
    }
}

.ui-accordion-header {
    cursor: pointer;
}

.b-tabs {
    &__anchor {
        color: color(grey, 2);
        font-weight: $font-main-bold;
        padding: baseline(2.5) rem(38);

        .ui-tabs-active & {
            color: color(black);
        }
    }

    &__item {
        border-bottom: 1px solid color(grey, 2);

        &:hover::after {
            background-color: color(grey, 2);
        }

        &::after {
            border-radius: rem(4) rem(4) 0 0;
            bottom: 0;
            content: "";
            height: rem(4);
            left: 0;
            position: absolute;
            width: 100%;
        }
    }

    &__content {
        margin-top: baseline(3);
    }

    &.ui-tabs-active {
        &::after {
            background-color: color(grey, 2);
        }
    }
}

.b-custom-tabs {
    &__list {
        display: flex;
        justify-content: flex-end;
        margin: 0 0 baseline(4);

        @media (--extra-small) {
            padding: 0 baseline(2);
        }
    }

    &__item {
        border: 1px solid color(grey, 2);
        border-width: 1px 1px 1px 0;
        transition: background-color $transition-duration-regular, font-weight $transition-duration-regular;

        &:first-child {
            border-width: 1px;
            border-radius: $border-radius-small 0 0 $border-radius-small;
        }

        &:last-child {
            border-radius: 0 $border-radius-small $border-radius-small 0;
        }

        &.ui-tabs-active,
        &:hover {
            background-color: color(grey, 4);
            font-weight: $font-main-bold;
        }

        @media (--extra-small) {
            text-align: center;
            width: 100%;
        }
    }

    &__link {
        display: block;
        color: color(black);
        min-width: rem(110);
        outline: none;
        padding: rem(10) rem(26);

        .non-touch &:hover {
            text-decoration: none;
        }
    }
}

.b-breadcrumbs {
    &__list {
        font-weight: $font-main-medium;
    }

    &__text,
    &__link,
    &__toggle {
        border-radius: $border-radius-small;
        font-size: $font-size-small;
        line-height: baseline(2);
        padding: baseline(1);

        &::before {
            color: $color-link;
            font-size: baseline(2);
        }
    }

    &__link,
    &__toggle {
        @include transition(background-color);

        &:active,
        .non-touch &:hover {
            background-color: color(black, $opacity: 0.05);
        }
    }

    &__text,
    &__link {
        &::before {
            margin-left: rem(-2);
            margin-right: baseline(1);
        }
    }

    &__toggle {
        @include b-toggle-button(
            $button-color: $color-link
        ) {
            right: baseline(1);
        }

        height: baseline(4);
        width: baseline(4);
    }
}

@media (--from-small) {
    .b-breadcrumbs {
        &__item {
            &--last {
                font-weight: $font-main-bold;
            }
        }

        &__text,
        &__link {
            &::before {
                font-size: rem(10);
            }
        }
    }
}

@media (--extra-small) {
    .b-breadcrumbs {
        &__list {
            background-color: color(grey, 4);
            font-weight: $font-main-bold;
            padding: baseline(1);

            &--content {
                @include transition((max-height, margin-top), $transition-duration-long);

                display: flex;
                max-height: 0;
                overflow: hidden;
                padding-bottom: 0;
                padding-top: 0;

                &::before,
                &::after {
                    content: "";
                    height: baseline(1);
                }

                &#{$const-expanded} {
                    margin-top: rem(1);
                    max-height: baseline(18);
                }
            }
        }
    }
}

.b-bnr--content {
    @media (--extra-small) {
        &:first-child {
            .b-bnr__inner {
                border-top: 0;
            }
        }
    }

    .b-bnr {
        &__inner {
            @include transition((background-color, box-shadow));

            position: relative;

            @media (--extra-small) {
                border-top: 1px solid color(grey, 3);
                display: flex;
                padding: baseline(3) 0;
            }

            &:hover {
                @media (--from-small) {
                    background-color: color(white);
                    box-shadow: $box-shadow-regular;
                }
            }
        }

        &__picture {
            @media (--extra-small) {
                flex-basis: baseline(11);
                flex-shrink: 0;
                margin-right: baseline(2);
            }
        }

        &__image {
            @media (--extra-small) {
                border-radius: $border-radius-small;
            }
        }

        &__content {
            @media (--from-small) {
                padding: baseline(3) baseline(4) baseline(4);
                text-align: center;
            }
        }

        &__title {
            font-size: $font-size-medium;
            font-weight: $font-main-bold;
            line-height: $line-height-medium;
            margin-bottom: baseline(1);
        }

        &__text {
            font-size: $font-size-small;
            line-height: $line-height-small;
        }
    }
}

.b-bnr--video {
    @media (--extra-small) {
        margin-bottom: baseline(5);

        &:last-child {
            margin-bottom: 0;
        }
    }

    .b-bnr {
        &__video {
            margin-bottom: baseline(3);
        }

        &__title {
            font-weight: $font-main-bold;
            line-height: $line-height-medium;
            margin-bottom: baseline(0.5);

            &-link {
                color: color(black);
            }
        }

        &__text {
            font-size: $font-size-small;
            line-height: $line-height-small;
        }
    }
}

.b-bnr--brand {
    margin-bottom: baseline(2.5);

    .non-touch &:hover {
        .b-bnr__inner {
            box-shadow: $box-shadow-regular;
        }
    }

    &.m-full {
        .b-bnr__inner {
            padding: 0;
        }
    }

    &.m-rectangle {
        .b-bnr__inner {
            padding: baseline(2);

            @media (--extra-small) {
                padding: baseline(1);
            }
        }
    }

    .b-bnr__inner {
        @include block-proportions(1/1);
        @include transition(box-shadow);

        align-items: center;
        border-radius: $border-radius-small;
        display: flex;
        overflow: hidden;
        padding: baseline(3);

        @media (--extra-small) {
            padding: baseline(2);
        }
    }

    &__image-1,
    &__image-2 {
        margin-bottom: 0;
        position: absolute;
        top: baseline(8);
        z-index: $z-min;
    }

    &__image-1 {
        left: 0;
        transform: translateX(-100%);
    }

    &__image-2 {
        right: 0;
        transform: translateX(100%);
    }
}

.b-bnr--brand-hero {
    .b-bnr {
        &__inner {
            height: baseline(50);
            overflow: hidden;
        }

        &__logo {
            position: relative;
            width: 100%;
            z-index: $z-small;

            @media (--small) {
                width: baseline(27);
            }
        }

        &__logo-wrapper {
            flex-shrink: 0; // IE fix
            margin-bottom: baseline(3);
            width: 100%; // IE fix

            @media (--extra-small) {
                align-items: flex-end;
                display: flex;
                height: baseline(21);
                margin-top: baseline(7);
                width: baseline(21);
            }
        }

        &__content {
            align-items: center;
            display: flex;
            flex-direction: column;
            height: 100%;
            justify-content: center;
            margin: 0 auto;
            max-width: rem(352);
            text-align: center;

            @media (--extra-small) {
                max-width: baseline(36);
            }

            @media (--small) {
                max-width: baseline(42);
            }

            @media (--from-medium) {
                position: relative;
                width: 30%;
            }
        }

        &__text {
            font-size: $font-size-small;
            width: 100%; // IE fix

            @media (--extra-small) {
                max-width: baseline(36);
            }
        }

        &__image {
            max-height: rem(336);
            max-width: none;
            position: absolute;
            transition: $transition-duration-regular;
            width: auto;
            z-index: $z-small;

            @media (--extra-small) {
                left: 50%;
                max-height: rem(270);
                transform: translateX(-50%);
            }

            &--left {
                bottom: 0;
                right: 135%;

                @media (--small) {
                    right: calc(100% - #{rem(197)});
                }

                @media (--medium) {
                    right: 125%;
                }
            }

            &--right {
                left: 135%;
                top: 0;

                @media (--small) {
                    left: calc(100% - #{rem(197)});
                }

                @media (--medium) {
                    left: 125%;
                }
            }

            &--center {
                top: 0;
            }
        }
    }
}

.b-bnr--brand-text {
    @include brand-text-color(color(white));

    .b-bnr {
        &__content {
            @include site-width;

            position: absolute;

            @media (--from-small) {
                top: 50%;
                transform: translateY(-50%);
                width: 50%;
            }

            @media (--extra-small) {
                top: baseline(12);
            }
        }

        &__title {
            font-size: $font-size-big;
            margin-bottom: baseline(2);
        }

        &__btn-wrapper {
            margin-top: baseline(3);
        }
    }
}

@each $colorName, $colorValue in $color-palette {
    .m-brand-text--#{$colorName} .b-bnr {
        @include brand-text-color(color($colorName, "base"));
    }

    @each $spectrumName, $spectrumValue in $colorValue {
        .m-brand-text--#{$colorName}-#{$spectrumName} .b-bnr {
            @include brand-text-color($spectrumValue);
        }
    }
}

$additional-colors: blue, teal, green, pink, purple;

.b-bnr--category {
    @media (--from-small) {
        display: flex;
        height: baseline(35);
        text-align: center;

        .b-bnr {
            &__inner {
                @include transition(background-color, $transition-duration-long);

                background-color: color(grey, 4);
                border-radius: $border-radius-small;
                flex: 1;
                padding: baseline(2);
                position: relative;
            }

            &__image,
            &__title {
                position: relative;
                z-index: $z-min;
            }

            &__image {
                @include transition(transform, $transition-duration-long);

                margin: 0 auto baseline(2);
                max-width: baseline(23.5);
            }

            &__content {
                display: flex;
                flex-direction: column;
                justify-content: center;
                min-height: baseline(5.5);
            }

            &__title {
                font-weight: $font-main-medium;
            }

            &__icon {
                border-radius: $border-radius-small;
                bottom: 0;
                color: color(yellow, light);
                font-size: baseline(26);
                left: 0;
                overflow: hidden;
                pointer-events: none;
                position: absolute;
                right: 0;
                top: 0;

                &::before {
                    @include transition((transform, opacity), $transition-duration-long);

                    left: 50%;
                    opacity: 0;
                    position: absolute;
                    top: 100%;
                    transform: translate3d(-50%, 0, 0);
                }
            }
        }

        &:hover {
            .b-bnr__inner,
            .b-bnr__image,
            .b-bnr__title,
            .b-bnr__icon::before {
                transition-duration: $transition-duration-regular;
            }

            .b-bnr__inner {
                background-color: color(yellow, tint);
            }

            .b-bnr__image {
                transform: scale(1.4) rotate(-22.5deg);
            }

            &:nth-child(odd) {
                .b-bnr__image {
                    transform: scale(1.4) rotate(22.5deg);
                }
            }

            .b-bnr__title {
                text-shadow: 0 0 0 $color-text;
            }

            .b-bnr__icon::before {
                opacity: 1;
                transform: translate3d(-50%, -35%, 0) scale(2);
            }
        }

        @each $color in $additional-colors {
            &.m-color-#{$color} {
                &:hover .b-bnr__inner {
                    background-color: color($color, tint);
                }

                .b-bnr__icon {
                    color: color($color, light);
                }
            }
        }
    }

    @media (--extra-small) {
        &.b-bnr {
            margin-bottom: 0;
            padding: 0;
        }

        .b-bnr {
            &__inner {
                align-items: center;
                border-top: 1px solid color(grey, 3);
                display: flex;
                padding-bottom: baseline(2);
                padding-top: baseline(2);
            }

            &__picture {
                order: 1;
                width: auto;
            }

            &__image {
                width: baseline(6);
            }

            &__content {
                flex: 1;
            }

            &__title {
                font-weight: $font-main-medium;
            }

            &__icon {
                display: none;
            }
        }
    }
}

.b-bnr--fav-characters {
    @include transition(box-shadow);

    border-radius: $border-radius-small;
    overflow: hidden;
    position: relative;

    &:hover {
        box-shadow: $box-shadow-regular;

        .b-bnr__picture {
            transform: scale(1);
        }
    }

    .b-bnr {
        &__picture {
            @include transition(transform);

            transform: scale(1.5);
            width: 100%;
        }

        &__content {
            @include fav-characters-color(color(white));

            bottom: 0;
            font-size: $font-size-larger;
            font-weight: $font-main-bold;
            line-height: $line-height-larger;
            padding: 0 baseline(3) baseline(4);
            position: absolute;
            text-align: center;
            width: 100%;

            // fix a gap between the shadow and the background that may appear during scale (especially in IE)
            &::before {
                content: "";
                height: rem(1);
                left: 0;
                position: absolute;
                right: 0;
                top: rem(-1);
            }

            @media (--small) {
                font-size: $font-size-large;
                line-height: $line-height-large;
            }
        }
    }
}

@each $colorName, $colorValue in $color-palette {
    .m-fav-character--#{$colorName} .b-bnr__content {
        @include fav-characters-color(color($colorName, "base"));
    }

    @each $spectrumName, $spectrumValue in $colorValue {
        .m-fav-character--#{$colorName}-#{$spectrumName} .b-bnr__content {
            @include fav-characters-color($spectrumValue);
        }
    }
}

$additional-colors: blue, yellow, teal, green, pink, purple;

.b-bnr--offers {
    @include block-proportions(420/244);

    display: flex;

    @media (--from-small) {
        margin: 0 rem(12);
        max-width: 30%;
        text-align: center;
        width: rem(244);
    }

    @media (--extra-small) {
        @include block-proportions(128/288);

        margin-top: baseline(3);

        &:first-of-type {
            margin-top: 0;
        }
    }

    .non-touch &:hover {
        .b-bnr__inner {
            box-shadow: $box-shadow-big;
        }

        .b-bnr__image {
            transform: scale(1.3);
            transition-duration: $transition-duration-regular;
        }
    }

    .b-bnr {
        &__inner {
            @include transition(box-shadow);

            background-color: color(grey, 4);
            border-radius: $border-radius-small;
            box-shadow: $box-shadow-regular;
            display: flex;
            flex: 1;
            flex-direction: column;
            justify-content: space-between;
            padding: baseline(3) baseline(2);
            position: relative;

            @media (--extra-small) {
                align-items: center;
                flex-direction: row-reverse;
                padding: baseline(1) rem(5) baseline(1) baseline(2);
            }
        }

        &__image,
        &__title,
        &__subtitle {
            position: relative;
            z-index: $z-min;
        }

        &__image {
            @include transition(transform);
        }

        &__picture {
            @media (--extra-small) {
                width: 47%;
            }
        }

        &__content {
            @media (--from-small) {
                min-height: baseline(15);
            }
        }

        &__title {
            @include t-hero-title;

            @media (--extra-small) {
                @include t-section-header;
            }
        }

        &__subtitle {
            @include t-section-header;

            font-weight: $font-main-medium;
        }

        &__title,
        &__subtitle {
            @media (--extra-small) {
                max-width: rem(120);
            }
        }

        &__icon {
            border-radius: $border-radius-small;
            bottom: 0;
            color: color(yellow, light);
            font-size: baseline(58);
            left: 0;
            overflow: hidden;
            pointer-events: none;
            position: absolute;
            right: 0;
            top: 0;

            &::before {
                bottom: 0;
                left: 50%;
                position: absolute;
                transform: translate(-50%, 40%) rotate(10deg);

                @media (--extra-small) {
                    bottom: 50%;
                    left: 0;
                    transform: translate(-50%, 50%) rotate(-20deg);
                }
            }

            &.i-shape-square::before {
                transform: translate(-55%, 55%) rotate(17deg);

                @media (--extra-small) {
                    transform: translate(-72%, 45%) rotate(-20deg);
                }
            }
        }
    }

    @each $color in $additional-colors {
        &.m-color-#{$color} {
            .b-bnr__inner {
                background-color: color($color, tint);
            }

            .b-bnr__icon {
                color: color($color, light);
            }
        }
    }

    &.m-color-pink {
        color: color(white);

        .b-bnr__icon {
            color: color(red);
        }
    }
}

.b-bnr--hero {
    .b-bnr {
        &__inner {
            height: baseline(50);
            overflow: hidden;
            position: relative;

            @media (--extra-small) {
                height: baseline(58);
            }

            @media (--small) {
                height: baseline(47);
            }
        }

        &__image {
            bottom: 0;
            left: 50%;
            max-height: baseline(44);
            position: absolute;
            transform: translateX(-60rem);
            transition: $transition-duration-regular;
            width: auto;
            z-index: $z-small;

            @media (--extra-small) {
                left: 50%;
                max-height: baseline(38);
                transform: translateX(-50%);
            }

            @media (--small) {
                max-height: 300px;
                transform: translateX(-37rem);
            }

            @media (--medium) {
                transform: translateX(-49rem);
            }

            @media (--large) {
                transform: translateX(-60rem);
            }
        }

        &__content {
            display: flex;
            flex-direction: column;
            height: 100%;

            @media (--extra-small) {
                padding: baseline(3) baseline(2);
                text-align: center;
            }

            @media (--from-small) {
                justify-content: center;
            }
        }

        &__title,
        &__text {
            left: 50%;
            position: relative;
            transition: $transition-duration-regular;

            @media (--extra-small) {
                left: 0;
            }

            @media (--small) {
                width: 36%;
            }

            @media (--medium) {
                transform: translateX(-9rem);
                width: 35%;
            }

            @media (--large) {
                max-width: 60rem;
                transform: translateX(-12rem);
                width: 30%;
            }
        }

        &__title {
            @include t-hero-title;

            color: color(blue);
            margin-bottom: baseline(2);
        }

        &__shape {
            font-size: rem(448);
            position: absolute;

            @media (--small) {
                font-size: rem(416);
            }

            &:nth-of-type(1) {
                bottom: -20%;
                left: 0;
                transform: translateX(-600px) rotate(45deg);

                @media (--extra-small) {
                    bottom: -38%;
                    font-size: rem(384);
                    left: 50%;
                    transform: translateX(-50%) scale(0.5);
                }

                @media (--small) {
                    bottom: -14%;
                    transform: translateX(-450px) rotate(45deg);
                }
            }

            &:nth-of-type(2) {
                right: 0;
                transform: translateX(600px) rotate(45deg);
                top: -20%;

                @media (--small) {
                    transform: translateX(450px) rotate(45deg);
                }

                @media (--extra-small) {
                    display: none;
                }
            }
        }

        &__icon {
            font-size: 8rem;
            position: absolute;
            transform: rotate(-15deg);
            z-index: $z-min;

            &:nth-of-type(1) {
                bottom: 11%;
                font-size: 7rem;
                left: 1%;
                transform: rotate(15deg);

                @media (--extra-small) {
                    bottom: 40%;
                    left: auto;
                    right: 75%;
                    transform: translate3d(110px, 100px, 0px) rotate(-360deg);
                }

                @media (--small) {
                    bottom: 4%;
                }

                @media (--large) {
                    left: calc(30% - 47rem);
                }
            }

            &:nth-of-type(2) {
                top: 23%;

                @media (--extra-small) {
                    display: none;
                }

                @media (--small) {
                    left: 5%;
                }

                @media (--medium) {
                    left: 6%;
                }

                @media (--large) {
                    left: calc(50% - 76rem);
                }
            }

            &:nth-of-type(3) {
                font-size: 7rem;
                right: 34%;
                top: 8%;

                @media (--extra-small) {
                    display: none;
                }

                @media (--small) {
                    right: 22%;
                }

                @media (--medium) {
                    right: 27%;
                }
            }

            &:nth-of-type(4) {
                right: 9%;
                top: 21%;
                transform: rotate(20deg);

                @media (--to-small) {
                    display: none;
                }

                @media (--medium) {
                    right: 4%;
                    top: 20%;
                }
            }

            &:nth-of-type(5) {
                bottom: 11%;
                right: 16%;

                @media (--extra-small) {
                    bottom: 24%;
                    left: 77%;
                    right: auto;
                    transform: translate3d(-110px, 100px, 0px) rotate(-360deg);
                }

                @media (--small) {
                    bottom: 4%;
                    left: 90%;
                }

                @media (--medium) {
                    right: 8%;
                }
            }
        }
    }
}

/*
<!-- SAMPLE #2 - simple banner, where final background image is applied for different screen sizes --> 
<div class="b-bnr g-grid__col-12 b-bnr--inlined">
    <div class="b-bnr__inner">
        <!-- PICTURE section is formed automatically from asset's Images custom fields (final background image is applied for different screen sizes) -->
        <picture class="b-bnr__picture">
            <source srcset="images/banners/content/banner-inlined-toys-bg-xs.jpg" media="(max-width : 767px)">
            <source srcset="images/banners/content/banner-inlined-toys-bg-s.jpg" media="(max-width : 1024px)">
            <img src="images/banners/content/banner-inlined-toys-bg-l.jpg" alt="Banner Inlined (Promo Toy)" class="b-bnr__image">
        </picture>
        <div class="b-bnr__content">
            <!-- BEGIN of content asset's BODY field (rest of additional wrappers are added automatically) -->
            <div class="b-bnr__textwrapper  m-clr--white">
                <h4 class="b-bnr__title">Promotion title</h4>
                <p class="b-bnr__text">Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam</p>
                <button class="f-cta-secondary"><span class="f-cta__text">Find out more</span></button>
            </div>
            <!-- END of content asset's BODY -->
        </div>
    </div>
</div>
*/

.b-bnr--inlined,
.b-bnr--inlined-complex {   // add .b-bnr--inlined so banner uses configured image as the fullwidth background
    border-radius: $border-radius-regular;

    @media (--extra-small) {
        border-radius: 0;
    }

    .b-bnr {
        &__inner,
        &__image {
            border-radius: $border-radius-regular;

            @media (--extra-small) {
                border-radius: 0;
            }
        }

        &__content {
            display: flex;
            flex-direction: column;
            height: 100%;
            position: absolute;
            top: 0;
            width: 100%;

            @media (--to-small) {
                padding: baseline(3) baseline(2);
                text-align: center;
            }

            @media (--from-medium) {
                justify-content: center;
            }
        }

        &__textwrapper {
            position: relative;
            width: 100%;
            z-index: $z-small;

            @media (--extra-small) {
                [class*="f-cta"] {
                    width: 100%;
                }
            }

            @media (--from-medium) {
                max-width: rem(400);
                width: 40%;
            }

            @media (--medium) {
                margin-left: baseline(5);
            }

            @media (--large) {
                margin-left: baseline(10);
            }
        }

        &__title {
            @include t-subpage-title;

            margin-bottom: baseline(2);
        }

        &__text {
            margin-bottom: baseline(2);
        }
    }
}

.b-bnr--inlined-complex { // use .b-bnr--inlined-complex for banner's wrapper to make it consist from separate parts (image, shapes etc)
    .b-bnr {
        &__inner {
            height: baseline(39);
            overflow: hidden;
            position: relative;

            @media (--extra-small) {
                height: baseline(59);
            }

            @media (--small) {
                height: baseline(65);
            }
        }

        &__image {
            max-height: baseline(33);
            position: absolute;
            right: 10%;
            top: 50%;
            transform: translateY(-50%);
            transition: $transition-duration-regular;
            width: auto;
            z-index: $z-small;

            @media (--extra-small) {
                max-height: baseline(22);
            }

            @media (--small) {
                max-height: baseline(32);
            }

            @media (--to-small) {
                bottom: baseline(1);
                left: 50%;
                top: auto;
                transform: translateX(-50%);
            }

            @media (--medium) {
                max-height: baseline(26);
            }
        }

        &__content {
            position: relative;
        }

        &__shape {
            bottom: -75%;
            font-size: rem(800);
            left: 56%;
            position: absolute;
            transform: rotate(55deg);
            transition: $transition-duration-regular;
            z-index: $z-min;

            @media (--extra-small) {
                bottom: -91%;
            }

            @media (--to-small) {
                font-size: rem(584);
                left: 50%;
                transform: translateX(-50%);
            }

            @media (--small) {
                bottom: -60%;
            }
        }
    }
}

section.css:
//----------------------------------------------------------------------
// Button layout
//----------------------------------------------------------------------
$btn-height-small : rem(44);
$btn-height-large : rem(56);

@mixin f-button-layout($btn-size: medium, $icon-size: default) {
    @include transition((background-color, border-color, color, box-shadow));

    border-radius: $border-radius-small;
    font-size: rem(16);
    font-weight: $font-main-bold;
    min-width: rem(152);
    padding: 0 rem(10);
    @if ($btn-size == medium) {
        $icon-gap: rem(8) !global;
        $icon-font-size: rem(16) !global;

        height: $btn-height-small;
        line-height: $btn-height-small;
    } @else if $btn-size == large {
        $icon-gap: rem(12) !global;
        $icon-font-size: rem(32) !global;

        height: $btn-height-large;
        line-height: $btn-height-large;
    }
    @if $icon-size == large {
        $icon-font-size: rem(32) !global;
    }

    &::before {
        margin-right: $icon-gap;
    }

    &::after {
        margin-left: $icon-gap;
    }

    &::before,
    &::after {
        line-height: inherit;
    }

    &[class*="i-svg-"],
    &[class*="i-social-"],
    &[class*="i-font-"] {
        text-align: initial;
    }

    &[class*="i-svg-"] {
        &::before,
        &::after {
            font-size: rem(43);
        }
    }

    &[class*="i-social-"] {
        &::before,
        &::after {
            font-size: rem(32);
        }
    }

    &:hover {
        text-decoration: none;
    }
}

@mixin f-state-disabled-style($bg-color : color(grey, 3), $color :color(grey)) {
    @include f-state-disabled {
        background-color: $bg-color;
        border-color: $bg-color;
        box-shadow: none;
        color: $color;
    }
}

@mixin f-button-shadow-style(
    $shadow-size-default: 0,
    $shadow-size-hover: -2px,
    $shadow-size-active: 1px
) {
    @include f-state-hover {
        box-shadow: inset 0 #{$shadow-size-hover} 0 color(black, $opacity: shadow);
    }
    @include f-state-active {
        box-shadow: inset 0 #{$shadow-size-active} 0 color(black, $opacity: shadow);
    }

    box-shadow: inset 0 #{$shadow-size-default} 0 color(black, $opacity: shadow);
}

.f-button,
%f-button {
    @include f-button-layout();
    @include f-button {
        background-color: color(grey, 4);
        border-color: color(grey, 2);
        color: color(black);
    }
    @include f-state-hover {
        background-color: color(blue, light);
        border-color: color(blue, light);
        color: color(white);
        text-decoration: none;
    }
    @include f-state-active {
        background-color: color(blue, light);
        border-color: color(blue, light);
        box-shadow: inset 0 2px 0 color(black, $opacity: shadow);
        color: color(white);
    }
    @include f-state-disabled-style();
}

.f-button-link,
%f-button-link {
    @include f-button-layout();
    @include f-button {
        background-color: color(white);
        border-color: transparent;
        color: color(blue);
    }
    @include f-state-hover {
        background-color: color(grey, 4);
        border-color: color(grey, 4);
    }
    @include f-state-active {
        background: color(blue, light);
        border-color: color(blue, light);
        box-shadow: inset 0 2px 0 color(black, $opacity: shadow);
    }
    @include f-state-disabled-style();
}

@mixin cta-primary-style() {
    @include f-button {
        background-color: color(green);
        border: 0;
        color: color(white);
        font-weight: $font-main-bold;
    }
    @include f-button-shadow-style(
        $shadow-size-default: -2px,
        $shadow-size-hover: -4px,
        $shadow-size-active: 2px
    );
    @include f-state-disabled-style();
}

.f-cta-primary,
%f-cta-primary {
    @include f-button-layout(large);
    @include cta-primary-style;
}

.f-cta-primary--small,
%f-cta-primary--small {
    @include f-button-layout(medium, large);
    @include cta-primary-style;
}

@mixin cta-secondary-style() {
    @include f-button() {
        background-color: color(grey, 4);
        border-color: color(black, $opacity: shadow);
        color: color(black);
    }
    @include f-button-shadow-style();
    @include f-state-disabled-style();
}

.f-cta-secondary,
%f-cta-secondary {
    @include f-button-layout(large);
    @include cta-secondary-style();
}

.f-cta-secondary--small,
%f-cta-secondary--small {
    @include f-button-layout(medium, large);
    @include cta-secondary-style();
}

@mixin cta-icon-layout() {
    min-width: rem(64);
    font-size: rem(22);

    &[class*="i-"] {
        text-align: center;
    }

    .f-button__text,
    .f-cta__text {
        @include visually-hidden;
    }

    &::before,
    &::after {
        margin-right: 0;
    }
}

.f-cta-icon-primary,
%f-cta-icon-primary {
    @include f-button-layout(large);
    @include cta-icon-layout();
    @include f-button-shadow-style();
    @include f-state-disabled-style($color: color(white));
    @include f-button() {
        background: color(blue, light);
        border-color: color(blue, light);
        color: color(white);
    }
}

.f-cta-icon-secondary,
%f-cta-icon-secondary {
    @include f-button-layout(large);
    @include cta-icon-layout();
    @include f-button-shadow-style();
    @include f-state-disabled-style($color: color(white));
    @include f-button() {
        background-color: color(grey, 4);
        border-color: color(grey, 2);
        color: color(grey, dim);
    }
    @include f-state-active {
        background-color: color(red);
        color: color(white);
    }
}

@mixin cta-icon-squircle-layout($btn-type: large, $btn-size: rem(48), $icon-size: rem(21)) {
    @if $btn-type == small {
        $btn-size : baseline(5);
        $icon-size: rem(18);
    }

    border-radius: 45%;
    font-size: 0;
    height: $btn-size;
    text-align: center;
    width: $btn-size;

    &::before,
    &::after {
        font-size: $icon-size;
        line-height: $btn-size / $icon-size;
    }
}

@mixin cta-icon-squircle-style() {
    @include transition((background-color, color));
    @include f-state-hover {
        background-color: color(green, light);
    }
    @include f-state-active {
        background-color: color(green, light);
    }

    background-color: color(blue, light);
    color: color(white);
}

.f-cta-icon-squircle,
%f-cta-icon-squircle {
    @include cta-icon-squircle-layout();
    @include f-state-disabled-style();
    @include cta-icon-squircle-style();
}

.f-cta-icon-squircle--small,
%f-cta-icon-squircle--small {
    @include cta-icon-squircle-layout(small);
    @include f-state-disabled-style();
    @include cta-icon-squircle-style();
}

.f-button-clear {
    @include f-button-clear;
}

.b-link,
%b-link {
    font-size: $font-size-regular;
    font-weight: $font-main-bold;
    line-height: $line-height-medium;
    color: $color-link;

    &:hover {
        text-decoration: underline;
    }
}

.b-link-text {
    @include transition;

    align-items: center;
    border: 1px solid color(grey, 2);
    border-radius: $border-radius-small;
    color: color(black);
    display: flex;
    font-size: $font-size-regular;
    font-weight: $font-main-bold;
    justify-content: center;
    line-height: 1;
    min-height: baseline(8);
    padding: baseline(2) baseline(3);

    @media (--extra-small) {
        padding: baseline(1.5);
    }

    &:hover {
        box-shadow: $box-shadow-regular;
        border-color: color(white);
        text-decoration: none;
    }

    &[class*=i-] {
        justify-content: space-between;

        &::before,
        &::after {
            font-size: baseline(6);
        }

        &::before {
            margin: 0 baseline(2) 0 baseline(-1);
        }

        &::after {
            margin: 0 baseline(-1) 0 baseline(2);
        }

        @media (--extra-small) {
            justify-content: center;

            &::after,
            &::before {
                display: none;
            }
        }
    }
}

.b-link-group {
    margin: baseline(3) 0;

    &__list {
        display: flex;

        &:first-child {
            .b-link-group__item {
                &:first-child {
                    border-radius: $border-radius-small 0 0 0;
                }

                &:last-child {
                    border-radius: 0 $border-radius-small 0 0;
                }
            }
        }

        &:last-child {
            .b-link-group__item {
                &:first-child {
                    border-radius: 0 0 0 $border-radius-small;
                }

                &:last-child {
                    border-radius: 0 0 $border-radius-small 0;
                }
            }
        }
    }

    &__item {
        @include transition;

        background-color: color(white);
        box-shadow: inset 0 0 0 1px color(grey, 2);
        flex: 1;
        margin: 0 -1px -1px 0;
        position: relative;

        &:hover {
            box-shadow: inset 0 0 0 1px color(blue, light);
            background-color: color(blue, tint);
            z-index: $z-min;
        }
    }

    &__link {
        @include transition(color);

        color: color(black);
        display: block;
        font-size: $font-size-medium;
        font-weight: $font-main-bold;
        line-height: $line-height-medium;
        padding: baseline(2.5);
        text-align: center;

        &:hover {
            color: color(blue);
            text-decoration: none;
        }
    }
}

