    @if $filename == search {
        $char: "";
    }
    @if $filename == show {
        $char: "";
    }
    @if $filename == sort {
        $char: "";
    }
    @if $filename == star {
        $char: "";
    }
    @if $filename == tick {
        $char: "";
    }
    @if $filename == user-filled {
        $char: "";
    }
    @if $filename == user {
        $char: "";
