.b-bnr--inlined,
.b-bnr--inlined-complex {   // add .b-bnr--inlined so banner uses configured image as the fullwidth background
    border-radius: $border-radius-regular;

    @media (--extra-small) {
        border-radius: 0;
    }

    .b-bnr {
        &__inner,
        &__image {
            border-radius: $border-radius-regular;

            @media (--extra-small) {
                border-radius: 0;
            }
        }

        &__content {
            display: flex;
            flex-direction: column;
            height: 100%;
            position: absolute;
            top: 0;
            width: 100%;

            @media (--to-small) {
                padding: baseline(3) baseline(2);
                text-align: center;
            }

            @media (--from-medium) {
                justify-content: center;
            }
        }

        &__textwrapper {
            position: relative;
            width: 100%;
            z-index: $z-small;

            @media (--extra-small) {
                [class*="f-cta"] {
                    width: 100%;
                }
            }

            @media (--from-medium) {
                max-width: rem(400);
                width: 40%;
            }

            @media (--medium) {
                margin-left: baseline(5);
            }

            @media (--large) {
                margin-left: baseline(10);
            }
        }

        &__title {
            @include t-subpage-title;

            margin-bottom: baseline(2);
        }

        &__text {
            margin-bottom: baseline(2);
        }
    }
}

.b-bnr--inlined-complex { // use .b-bnr--inlined-complex for banner's wrapper to make it consist from separate parts (image, shapes etc)
    .b-bnr {
        &__inner {
            height: baseline(39);
            overflow: hidden;
            position: relative;

            @media (--extra-small) {
                height: baseline(59);
            }

            @media (--small) {
                height: baseline(65);
            }
        }

        &__image {
            max-height: baseline(33);
            position: absolute;
            right: 10%;
            top: 50%;
            transform: translateY(-50%);
            transition: $transition-duration-regular;
            width: auto;
            z-index: $z-small;

            @media (--extra-small) {
                max-height: baseline(22);
            }

            @media (--small) {
                max-height: baseline(32);
            }

            @media (--to-small) {
                bottom: baseline(1);
                left: 50%;
                top: auto;
                transform: translateX(-50%);
            }

            @media (--medium) {
                max-height: baseline(26);
            }
        }

        &__content {
            position: relative;
        }

        &__shape {
            bottom: -75%;
            font-size: rem(800);
            left: 56%;
            position: absolute;
            transform: rotate(55deg);
            transition: $transition-duration-regular;
            z-index: $z-min;

            @media (--extra-small) {
                bottom: -91%;
            }

            @media (--to-small) {
                font-size: rem(584);
                left: 50%;
                transform: translateX(-50%);
            }

            @media (--small) {
                bottom: -60%;
            }
        }
    }
}
